'use strict';

var SectionList = require('./section-list'),
    xhr = require('./xhr'),
    byId = document.getElementById.bind(document),
    byClass = document.getElementsByClassName.bind(document);

var RunStates = {
    RUNNING: 'running',
    PENDING: 'pending'
};

function Controller() {
    this._sections = new SectionList(this);
    this._runButton = byId('run');
    this._runFailedButton = byId('runFailed');
    this._hostInput = byId('viewHostInput');

    this.state = RunStates.PENDING;

    if (byId('run')) {
        this._handleButtonClicks();
    }
    this._listenForEvents();
    this._handleHostChange();
}

Controller.prototype = {
    runState: function(state) {
        this.run(state);
    },

    _runAllFailed: function() {
        var failed = this._sections.findStates('fail');

        if (failed.length) {
            this.run(failed);
        }
    },

    run: function(failed) {
        var _this = this;

        this._toggleButtons(false);
        this.state = RunStates.RUNNING;

        xhr.post('/run', failed, function(error) {
            if (error) {
                this.state = RunStates.PENDING;
                return;
            }
            return failed ? _this._sections.markAsQueued(failed) : _this._sections.markAllAsQueued();
        });
    },

    _toggleButtons: function(isEnabled) {
        Array.prototype.forEach.call(byClass('button_togglable'), function(element) {
            element.disabled = !isEnabled;
        });

        this._sections.toggleRetry(isEnabled);
    },

    _handleButtonClicks: function() {
        var sections = this._sections,
            _this = this;

        byId('expandAll').addEventListener('click', sections.expandAll.bind(sections));
        byId('collapseAll').addEventListener('click', sections.collapseAll.bind(sections));
        byId('expandErrors').addEventListener('click', sections.expandErrors.bind(sections));

        this._runButton.addEventListener('click', function() {
            // TODO: Maybe only get all sections manually when a specific suite is selected?
            _this.run(_this._sections.findStates());
        });
        this._runFailedButton.addEventListener('click', this._runAllFailed.bind(this));
    },

    _handleHostChange: function() {
        var sections = this._sections,
            _this = this;

        this._hostInput.addEventListener('change', function() {
            sections.setViewLinkHost(_this._hostInput.value);
            // will save host to local storage
            if (window.localStorage) {
                window.localStorage.setItem('_gemini-replace-host', _this._hostInput.value);
            }
        });

        // read saved host from local storage
        if (window.localStorage) {
            var host = window.localStorage.getItem('_gemini-replace-host');
            if (host) {
                sections.setViewLinkHost(host);
                _this._hostInput.value = host;
            }
        }
    },

    _listenForEvents: function() {
        var eventSource = new EventSource('/events'),
            _this = this;

        eventSource.addEventListener('beginSuite', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({suite: data.suite});

            if (section && section.status === 'queued') {
                section.status = 'running';
            }
        });

        eventSource.addEventListener('beginState', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state
                });

            if (section && section.status === 'queued') {
                section.status = 'running';
            }
        });

        eventSource.addEventListener('endTest', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state,
                    browserId: data.browserId
                });

            data.metaInfo = data.state && data.state.metaInfo
                ? JSON.stringify(data.state.metaInfo, null, 4)
                : 'Meta info is not available';

            if (data.equal) {
                section.setAsSuccess(data);
            } else {
                section.setAsFailure(data);
                section.expand();
                _this._sections.markBranchAsFailed(section);
            }
        });

        eventSource.addEventListener('skipState', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state,
                    browserId: data.browserId
                });
            section.setAsSkipped();
            var stateSection = _this._sections.findSection({
                suite: data.suite,
                state: data.state
            });

            _this._sections.markIfFinished(stateSection);
        });

        eventSource.addEventListener('err', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state,
                    browserId: data.browserId
                });
            section.setAsError({stack: data.stack});
            section.expand();
            _this._sections.markBranchAsFailed(section);
        });

        eventSource.addEventListener('noReference', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state,
                    browserId: data.browserId
                });

            section.setAsNewReference(data);
            section.expand();
            _this._sections.markBranchAsFailed(section);
        });

        eventSource.addEventListener('endState', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state
                });

            _this._sections.markIfFinished(section);
        });

        eventSource.addEventListener('endSuite', function(e) {
            var data = JSON.parse(e.data),
                section = _this._sections.findSection({
                    suite: data.suite,
                    state: data.state,
                    browserId: data.browserId
                });

            _this._sections.markIfFinished(section);
        });

        eventSource.addEventListener('end', function() {
            _this._toggleButtons(true);
            _this.state = RunStates.PENDING;
        });
    }
};

module.exports = Controller;
'use strict';

var Section = require('./section'),
    Index = require('../common/tests-index'),
    map = Array.prototype.map,
    every = Array.prototype.every;

function SectionList(controller) {
    this._controller = controller;
    this._sectionsIndex = new Index();
    this._sections = map.call(document.querySelectorAll('.section'), function(node) {
        var parentNode = this._findParentSectionNode(node),
            section = new Section(node, parentNode && this._sectionForNode(parentNode), this._onRunState.bind(this));

        this._sectionsIndex.add(section);
        return section;
    }, this);
}

SectionList.prototype = {
    constructor: SectionList,

    expandAll: function() {
        this._sections.forEach(function(section) {
            section.expand();
        });
    },

    collapseAll: function() {
        this._sections.forEach(function(section) {
            section.collapse();
        });
    },

    expandErrors: function() {
        this._sections.forEach(function(section) {
            section.expandIfError();
        });
    },

    markAsQueued: function(leafQueries) {
        leafQueries = [].concat(leafQueries);

        var _this = this;

        this._sectionsIndex.find(leafQueries)
            .forEach(function(section) {
                section.setAsQueued();

                while ((section = _this.findParent(section))) {
                    section.status = 'queued';
                }
            });
    },

    markAllAsQueued: function() {
        this._sections.forEach(function(section) {
            section.status = 'queued';
        });
    },

    markIfFinished: function(section) {
        if (section.status === 'fail') {
            //already marked as fail
            return;
        }
        var nodes = section.domNode.querySelectorAll('.section');
        var allChildrenFinished = every.call(nodes, function(node) {
            return this._sectionForNode(node).isFinished();
        }, this);

        if (allChildrenFinished) {
            section.status = 'success';
        }
    },

    markBranchAsFailed: function(fromSection) {
        while ((fromSection = this.findParent(fromSection))) {
            fromSection.status = 'fail';
            fromSection.expand();
        }
    },

    findSection: function(query) {
        return this._sectionsIndex.find(query);
    },

    findParent: function(section) {
        if (section.browserId) {
            return this.findSection({
                suite: section.suite,
                state: section.state
            });
        }

        if (section.state && section.state.name) {
            return this.findSection({suite: section.suite});
        }
        var parentSectionNode = this._findParentSectionNode(section.domNode);
        if (parentSectionNode) {
            return this._sectionForNode(parentSectionNode);
        }
        return null;
    },

    findStates: function(sectionStatus) {
        return this._sections
            .filter(function(section) {
                if (sectionStatus) {
                    return section.browserId && section.status === sectionStatus;
                }
                return section.browserId;
            })
            .map(function(section) {
                return {
                    suite: section.suite,
                    state: section.state,
                    browserId: section.browserId
                };
            });
    },

    toggleRetry: function(isEnabled) {
        this._sections.forEach(function(section) {
            section.toggleRetry(isEnabled);
        });
    },

    setViewLinkHost: function(host) {
        this._sections.forEach(function(section) {
            section.setViewHost(host);
        });
    },

    _findParentSectionNode: function(node) {
        while ((node = node.parentNode)) {
            if (node.classList && node.classList.contains('section')) {
                return node;
            }
        }
        return null;
    },

    _sectionForNode: function(domNode) {
        var query = {
            suite: {path: domNode.getAttribute('data-suite-path')},
            state: {name: domNode.getAttribute('data-state-name')},
            browserId: domNode.getAttribute('data-browser-id')
        };

        return this.findSection(query);
    },

    _onRunState: function(state) {
        this._controller.runState(state);
    }
};

module.exports = SectionList;
'use strict';
var SuiteControls = require('./suite-controls'),
    successTemplate = require('../views/partials/success-result.hbs'),
    failTemplate = require('../views/partials/fail-result.hbs'),
    errorTemplate = require('../views/partials/error-result.hbs'),
    skipTemplate = require('../views/partials/skip-result.hbs'),
    noReferenceTemplate = require('../views/partials/no-reference-result.hbs'),
    xhr = require('./xhr'),
    url = require('url'),
    _ = require('lodash'),
    Clipboard = require('clipboard');

var statusList = [
    'idle',
    'queued',
    'running',
    'success',
    'fail',
    'skip'
];

function statusClass(status) {
    return 'section_status_' + status;
}

function getInitialStatus(sectionNode) {
    var status;
    for (var i = 0; i < statusList.length; i++) {
        status = statusList[i];
        if (sectionNode.classList.contains(statusClass(status))) {
            return status;
        }
    }
    return null;
}

function Section(node, parent, runStateHandler) {
    this.suite = {path: node.getAttribute('data-suite-path')};
    this.state = {name: node.getAttribute('data-state-name')};
    this.browserId = node.getAttribute('data-browser-id');
    this.domNode = node;
    this._status = getInitialStatus(node);
    this._titleNode = node.querySelector('.section__title');
    this._bodyNode = node.querySelector('.section__body');
    this._titleNode.addEventListener('click', this.toggle.bind(this));
    this._parent = parent;
    this._runStateHandler = runStateHandler;
    this._suiteControls = null;
    this._viewLink = node.querySelector('[data-suite-view-link]');
    this._clipboardBtn = node.querySelector('.button[data-clipboard-text]');

    if (this._clipboardBtn) {
        /* eslint-disable no-new */
        new Clipboard(this._clipboardBtn);
    }

    // turning off event bubbling when click button
    Array.prototype.forEach.call(node.querySelectorAll('.button'), function(elem) {
        elem.addEventListener('click', function(e) {
            e.stopPropagation();
        });
    });
}

Section.prototype = {
    constructor: Section,
    expand: function() {
        this.domNode.classList.remove('section_collapsed');
    },

    collapse: function() {
        this.domNode.classList.add('section_collapsed');
    },

    toggle: function() {
        this.domNode.classList.toggle('section_collapsed');
    },

    get status() {
        return this._status;
    },

    set status(value) {
        if (this._status) {
            this.domNode.classList.remove(statusClass(this._status));
        }

        this._status = value;
        this.domNode.classList.add(statusClass(this._status));
    },

    expandIfError: function() {
        if (this.status === 'fail') {
            this.expand();
        } else {
            this.collapse();
        }
    },

    setAsQueued: function() {
        this.status = 'queued';
        while (this._bodyNode.hasChildNodes()) {
            this._bodyNode.removeChild(this._bodyNode.firstChild);
        }
    },

    setAsFailure: function(results) {
        this.status = 'fail';
        this._bodyNode.innerHTML = failTemplate(results);

        this._suiteControls = new SuiteControls(this._bodyNode);
        this._suiteControls.setAsFailure(this.retry.bind(this), this.updateReference.bind(this));
    },

    setAsSuccess: function(results) {
        var failedChild = this.domNode.querySelector('.section.' + statusClass('fail'));
        if (failedChild) {
            return;
        }

        this.status = 'success';
        if (results) {
            this._bodyNode.innerHTML = successTemplate(results);

            this._suiteControls = new SuiteControls(this._bodyNode);
            this._suiteControls.setAsSuccess(this.retry.bind(this));
        }

        if (this._parent && this._parent.status === 'fail') {
            this._parent.setAsSuccess();
        }
    },

    setAsSkipped: function() {
        this.status = 'skipped';
        this._bodyNode.innerHTML = skipTemplate();
    },

    setAsError: function(error) {
        this.status = 'fail';
        this._bodyNode.innerHTML = errorTemplate(error);
    },

    setAsNewReference: function(result) {
        this.status = 'fail';
        this._bodyNode.innerHTML = noReferenceTemplate(result);

        this._suiteControls = new SuiteControls(this._bodyNode);
        this._suiteControls.setAsNewReference(this.retry.bind(this), this.updateReference.bind(this));
    },

    isFinished: function() {
        return this.status !== 'queued' &&
            this.status !== 'running';
    },

    updateReference: function() {
        var _this = this,
            postData = this._collectStateData();
        xhr.post('/update-ref', postData, function(error, response) {
            if (!error) {
                _this.setAsSuccess(response);
            }
        });
    },

    retry: function() {
        this._runStateHandler(this._collectStateData());
    },

    toggleRetry: function(isEnabled) {
        if (this._suiteControls) {
            this._suiteControls.toggleRetry(isEnabled);
        }
    },

    setViewHost: function(host) {
        if (!this._viewLink) {
            return;
        }
        var href = this._viewLink.dataset.suiteViewLink,
            parsedHost;

        if (host) {
            parsedHost = url.parse(host, false, true);
            // extending current url from entered host
            href = url.format(_.assign(
                url.parse(href),
                {
                    host: parsedHost.slashes ? parsedHost.host : host,
                    protocol: parsedHost.slashes ? parsedHost.protocol : null,
                    hostname: null,
                    port: null
                }
            ));
        }
        this._viewLink.setAttribute('href', href);
    },

    _collectStateData: function() {
        return {
            suite: this.suite,
            state: this.state,
            browserId: this.browserId
        };
    }
};

module.exports = Section;
'use strict';

function SuiteControls(parentNode) {
    this._acceptButton = parentNode.querySelector('.suite-controls__accept');
    this._retryButton = parentNode.querySelector('.suite-controls__retry');
}

SuiteControls.prototype = {
    setAsNewReference: function(retryHandler, acceptHandler) {
        this._setButtonEnabled(this._acceptButton, true);
        this._attachHandlers(retryHandler, acceptHandler);
    },

    setAsSuccess: function(retryHandler) {
        this._setButtonVisible(this._acceptButton, false);
        this._enableIfNotRunning(this._retryButton);
        this._attachHandlers(retryHandler);
    },

    setAsFailure: function(retryHandler, acceptHandler) {
        this._setButtonEnabled(this._acceptButton, true);
        this._enableIfNotRunning(this._retryButton);
        this._attachHandlers(retryHandler, acceptHandler);
    },

    toggleRetry: function(isEnabled) {
        this._setButtonEnabled(this._retryButton, isEnabled);
    },

    _enableIfNotRunning: function(button) {
        var isRunning = window.controller.state === 'running';

        this._setButtonEnabled(button, !isRunning);
    },

    _setButtonEnabled: function(button, isEnabled) {
        if (isEnabled) {
            button.classList.remove('suite-controls__item_disabled');
        } else {
            button.classList.add('suite-controls__item_disabled');
        }

        button.disabled = !isEnabled;
    },

    _setButtonVisible: function(button, isVisible) {
        if (isVisible) {
            button.classList.remove('suite-controls__item_hidden');
        } else {
            button.classList.add('suite-controls__item_hidden');
        }
    },

    _attachHandlers: function(retryHandler, acceptHandler) {
        if (retryHandler) {
            this._retryButton.addEventListener('click', retryHandler);
        }

        if (acceptHandler) {
            this._acceptButton.addEventListener('click', acceptHandler);
        }
    }
};

module.exports = SuiteControls;
'use strict';

exports.post = function(url, data, callback) {
    if (!callback) {
        callback = data;
        data = null;
    }
    var xhr = new XMLHttpRequest();
    xhr.open('POST', url, true);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.onload = function() {
        var data = JSON.parse(xhr.responseText);
        if (xhr.status === 200) {
            callback(null, data);
        } else {
            callback(new Error(data.error));
        }
    };

    if (data) {
        xhr.send(JSON.stringify(data));
    } else {
        xhr.send();
    }
};
'use strict';

var _ = require('lodash');

function TestsIndex() {
    this._index = {};
}

TestsIndex.prototype = {
    constructor: TestsIndex,

    add: function(item) {
        if (!item.suite || item.suite.path === null) {
            return;
        }

        var indexData = this._index[item.suite.path];
        if (!indexData) {
            indexData = this._index[item.suite.path] = {
                suite: null,
                states: {}
            };
        }

        if (!item.state || item.state.name === null) {
            indexData.suite = item;
            return;
        }

        var stateData = indexData.states[item.state.name];
        if (!stateData) {
            stateData = indexData.states[item.state.name] = {
                state: null,
                browsers: {}
            };
        }

        if (!item.browserId) {
            stateData.state = item;
            return;
        }
        stateData.browsers[item.browserId] = item;
    },

    find: function(query) {
        if (Array.isArray(query)) {
            return _(query)
                .map(this.find, this)
                .compact()
                .value();
        }

        var indexData = query.suite && this._index[query.suite.path];
        if (!indexData) {
            return null;
        }
        if (!query.state || query.state.name === null) {
            return indexData.suite;
        }
        var stateData = indexData.states[query.state.name];
        if (!stateData) {
            return null;
        }

        if (!query.browserId) {
            return stateData.state;
        }

        return stateData.browsers[query.browserId] || null;
    }
};

module.exports = TestsIndex;
